mapper.select:
SELECT * 
FROM SYS_ROLE a
WHERE id = #{id}

mapper.update:
UPDATE SYS_ROLE a SET
	a.`name` = #{name},
	a.`sort` = #{sort},
	a.`description` = #{description},
	a.isEnable = #{isEnable}
WHERE a.`id` = #{id}

UPDATE SYS_ROLE a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.delete:
DELETE FROM SYS_ROLE

	
		WHERE 1 != 1
	
	
		WHERE `id` = #{id}
	
	
		WHERE `id` in #{id}
