mapper.insert:
INSERT INTO SYS_RE_ROLE_PERMISSION(
	appId,
	roleId,
	permissionId
) 
VALUES (
	#{appId},
	#{roleId},
	#{permissionId}
)

mapper.delete:
DELETE FROM SYS_RE_ROLE_PERMISSION
WHERE 1 = 1

	AND permissionId in #{item}

DELETE FROM SYS_RE_ROLE_PERMISSION
WHERE 1 = 1

	AND roleId in #{item}

DELETE FROM SYS_RE_ROLE_PERMISSION
WHERE 1 = 1

	AND appId in #{item}

DELETE FROM SYS_RE_ROLE_PERMISSION
WHERE 1 = 1
AND appId = #{appId}
AND roleId = #{roleId}
