mapper.select:
SELECT * 
FROM SYS_USER a
WHERE id = #{id}

mapper.update:
UPDATE SYS_USER a SET
	a.isEnable = #{isEnable}
WHERE 1 = 1

	AND a.`id` in #{item}

UPDATE SYS_USER a SET
	a.password = #{password}
WHERE 1 = 1

	AND a.`id` in #{item}

mapper.delete:
DELETE FROM SYS_USER

	
		WHERE 1 != 1
	
	
		WHERE `id` = #{id}
	
	
		WHERE `id` in #{id}
