mapper.select:
   
		SELECT * 
     	FROM SYS_RE_USER_APP a
     	WHERE 1 = 1
     	
			AND a.userId = #{userId} 
		
		
			AND a.appId = #{appId}

mapper.insert:
INSERT INTO SYS_RE_USER_APP(
	appId,
	userId
) 
VALUES (
	#{appId},
	#{userId}
)

mapper.delete:
DELETE FROM SYS_RE_USER_APP
WHERE 1 = 1

	AND userId in #{item}

DELETE FROM SYS_RE_USER_APP
WHERE 1 = 1

	AND appId in #{item}
