mapper.select:
   
		SELECT * 
     	FROM SYS_RE_USER_ROLE a
     	WHERE 1 = 1
     	
			AND a.userId = #{userId} 
		
		
			AND a.roleId = #{roleId}

mapper.insert:
INSERT INTO SYS_RE_USER_ROLE(
	userId,
	roleId
) 
VALUES (
	#{userId},
	#{roleId}
)

mapper.delete:
DELETE FROM SYS_RE_USER_ROLE
WHERE 1 = 1

	AND roleId in #{item}

DELETE FROM SYS_RE_USER_ROLE
WHERE 1 = 1

	AND userId in #{item}
